// Create and append slip js stuff to the Visits list
var ol = $('#Visits #list_visits ul')[0];

ol.addEventListener('slip:beforereorder', function(e){
  if (/demo-no-reorder/.test(e.target.className)) {
    e.preventDefault();
  }
}, false);

ol.addEventListener('slip:beforewait', function(e){
  if (e.target.className.indexOf('instant') > -1) e.preventDefault();
}, false);

ol.addEventListener('slip:reorder', function(e){
  var ul = e.target.parentNode;
  var apts = cti.store.variables.v_selectedDayAppointments;

  apts.splice(e.detail.spliceIndex, 0, apts.splice(e.detail.originalIndex, 1)[0]);
  for (var i = 0; i < apts.length; i++) {
    apts[i].index = i;
  }
  cti.utils.updatePage();

  cti.utils.callAction("call-function", {"name":"fn_render_map"});

  return false;
}, false);

