window.pouchManager.loadFromDatabase('appointments', function (doc) {
  var appointments = doc.data

  var visitObj = JSON.parse(JSON.stringify(cti.store.variables.selected_visit))

  // On a successful match append the patientData to the selected_visit
  for(var i in appointments) {
    if(appointments[i].appointmentId == cti.store.variables.selected_visit.appointmentId) {
      cti.store.variables.selected_visit = appointments[i]
      break
    }
  }

  cti.store.variables.selected_visit.displayName = visitObj.displayName
  cti.store.variables.selected_visit.nhsNumFormatted = visitObj.nhsNumFormatted

  // Prepare the timeline and form variables
  cti.fn_prep_timeline()
  cti.fn_prep_forms(cti.store.variables.selected_visit)

  // Clear down all appointment stacks
  delete cti.store.variables.v_selectedDayAppointments
  delete cti.store.variables.v_appointments

  // Reset form controls
  delete cti.store.pages.DialogFormOne
  cti.store.variables.dialogFormFirstRun = false

  // Navigate to the Visit page
  cti.utils.callAction("go-to-page", {"name":"Visit"})
  cti.fn_toggle_spinner('close')
}, function (reason, err) {
  window.logManager.consoleLog(
    'Pouch DB: Failed retriveing Appointments Data from Pouch - ' + reason + err, 
    'info'
  )
  cti.fn_toggle_spinner('close')
})

