// gotFile callback function
//
// Success callback function to use the fileEntry data to convert the
// raw image file to base64
function gotFile(fileEntry) {
  window.logManager.consoleLog('Photo Load: Converting file store to base64...', 'info')

  // Gain access to the root file URL
  fileEntry.file(function(file) {
    // Set new FileReader
    var reader = new FileReader()
    // Attach callback function
    reader.onloadend = function(e) {
      // ... on success generate API friendly data
      window.logManager.consoleLog('Photo Load: File converted to Base64', 'info')

      // Convert the binary back into a string
      var dataURL = window.btoa(e.target.result)

      // Set the base64 string
      var base64 = dataURL.replace(/^data:image\/[a-z]+;base64,/, '')

      // Split down the detail so that we can catch the file type
      /*
      var detail = ""
      if(this.result.indexOf("base64,") != -1) {
        detail = this.result.split("base64,")
      }
      var fileType = ""
      if(detail[0].indexOf("/") != -1) {
        fileType = detail[0].split("/")
      }
      var extension = ""
      if(fileType[1].indexOf(";") != -1) {
        extension = fileType[1].split(";")
      }
      */
      var fileExtension = 'jpg'

      // RiO does not accept jpeg so split out the e to
      // pass as jpg
      if (fileExtension == 'jpeg') {
        fileExtension = fileExtension.replace('e', '')
      }

      // Set the local variables
      cti.store.media.btnPhoto.base64 = base64
      cti.store.media.btnPhoto.fileExtension = fileExtension

      // Call success to kick off action flow
      $success()
    }

    // Convert the file to base64 string...
    reader.readAsBinaryString(file)
  })
}

// fail callback function
//
// Fail callback function to inform the user that the file has failed to
// resolve from the local file system
function fail(error) {
  window.logManager.consoleLog('Photo Load: Failed - Reason: ' + error, 'error')

  // Remove any corrupt trace of the file
  if (cti.store.media && cti.store.media.btnPhoto && cti.store.media.btnPhoto.images && cti.store.media.btnPhoto.images.length === 1) {
    delete cti.store.media.btnPhoto
  }

  // Notify the user
  cti.utils.callAction('show-notification', {
    name: 'notification_error',
    title: 'Error Locating Image',
    text: 'The application was unable to locate the image. Please retake the photo and try again.'
  })

  // Close the spinner
  cti.fn_toggle_spinner('close', '')
}

