if (cti.store.media && cti.store.media.btnPhoto && cti.store.media.btnPhoto.images && cti.store.media.btnPhoto.images.length === 1) {
  // Check that we are running  on a device compatible with image to
  // base64 conversion...
  if (cordova && window && window.resolveLocalFileSystemURL) {
    // Grab the image in a local variable
    var photo = cti.store.media.btnPhoto.images[0]

    // Then pass it to resolveLocalFileSystemURL to convert it as base64
    window.resolveLocalFileSystemURL(photo, gotFile, fail)
  } else {
    // ... otherwise we ignore the file and process the submission
    // without any documents
    window.logManager.consoleLog("Photo Load: Ignoring file upload as we're in studio.", 'info')

    // Remove any trace of the photo
    delete cti.store.media.btnPhoto

    // Call success to kick off action flow
    $success()
  }
} else {
  // Call success to kick off action flow
