}

var arrivalTime = cti.store.pages.Outcome.arrivalTime;
var actualTime = cti.store.pages.Outcome.actualTime;
var currentTime = moment().format("HH:mm");

if(actualTime < arrivalTime) {
  cti.utils.callAction("show-notification", {
    "name": "notification_warning", 
    "title" : "Warning: Time Error.", 
    "text" : "The Patients Seen At Time can not be before the Patients Arrival Time."
  });
  setTime();
} else if(actualTime > currentTime) {
  cti.utils.callAction("show-notification", {
    "name": "notification_warning", 
    "title" : "Warning: Time Error.", 
    "text" : "The Patients Seen At Time can not be after the current time."
  });
  setTime();
} else {
  var now = moment().format("HH:mm");
  var time = moment.utc(moment(now,"HH:mm").diff(moment(actualTime,"HH:mm"))).format("HH:mm");
  var mins = moment(time, "HH:mm").format("m");
  var hours = moment(time, "HH:mm").format("H");

  cti.store.pages.Outcome.duration_hours = {"label": hours, "value": hours };
  cti.store.pages.Outcome.duration_minutes = {"label": mins, "value": mins };
}

