var weekday = new Array(7);
weekday[1]="Monday";
weekday[2]="Tuesday";
weekday[3]="Wednesday";
weekday[4]="Thursday";
weekday[5]="Friday";
weekday[6]="Saturday";
weekday[0]="Sunday";

var day = weekday[cti.store.variables.v_selectedDayOfWeek];

for(var i in templates) {
  if(templates[i].name == day) {
    var sessions = templates[i].sessions;
    break;
  }
}

for(var x in sessions) {
  var sessionStartHours = moment(sessions[x].startTime, 'HH:mm:ss').format('HH');

  var sessionEndHours = moment(sessions[x].endTime, 'HH:mm:ss').format('HH');
  var sessionEndMins = moment(sessions[x].endTime, 'HH:mm:ss').format('mm');

  var hours = moment(startTime, 'HH:mm:ss').format('HH');
  var mins = moment(startTime, 'HH:mm:ss').format('mm');

  if(hours >= sessionStartHours && (hours < sessionEndHours || hours === sessionEndHours && sessionEndMins <= 30)) {
    var obj = {
      label: sessions[x].slotLength + ' minutes',
      value: sessions[x].slotLength
    };
    cti.store.variables.v_intended_durations = []
    cti.store.variables.v_intended_durations.push(obj);
    break;
  }
}

