// Check fileType of document...
if(cti.store.variables.v_selectedDocument.fileType == "image/jpeg") {
  // Load Spinner
  cti.fn_toggle_spinner('open', 'Loading Image...');

  // ... if type of jpeg process under the embed tag
  // Grab the page div element for display
  var imageDiv = document.getElementById('image');
  imageDiv.classList.add("doc-image-class");

  var img = document.createElement('img');
  img.id = "embed-doc";
  img.style.width = "100%"
  img.style.height = "100%"
  img.src = base64Data;
  img.type = cti.store.variables.v_selectedDocument.fileType;
  imageDiv.appendChild(img);

  // Close Spinner
  cti.fn_toggle_spinner('close', '', 1000);
} else if(cti.store.variables.v_selectedDocument.fileType == "image/png") {
  // Load Spinner
  cti.fn_toggle_spinner('open', 'Loading Image...');

  // ... if type of jpeg process under the embed tag
  // Grab the page div element for display
  var imageDiv = document.getElementById('image');
  imageDiv.classList.add("doc-image-class");

  var img = document.createElement('img');
  img.id = "embed-doc";
  img.style.width = "100%"
  img.style.height = "100%"
  img.src = base64Data;
  img.type = cti.store.variables.v_selectedDocument.fileType;
  imageDiv.appendChild(img);

  // Close Spinner
  cti.fn_toggle_spinner('close', '', 1000);
} else if(cti.store.variables.v_selectedDocument.fileType == "application/pdf") {
  // Load Spinner
  cti.fn_toggle_spinner('open', 'Loading PDF...');

  var pdfDiv = document.getElementById('pdf');
  pdfDiv.classList.add("doc-image-class");

  base64Data = base64Data.replace(/^[^,]+,/, '');
  var pdfData = atob(base64Data);

  PDFJS.workerSrc = userAssetLocation+'pdf.worker.js'

  var loadingTask = PDFJS.getDocument({data: pdfData});
  loadingTask.promise.then(function(pdf) {
    var pageNumber = 1;
    pdf.getPage(pageNumber).then(function(page) {
      // Customised viewport setting
      var desiredWidth = screen.width - 20;
      var viewport = page.getViewport(1);
      var scale = desiredWidth / viewport.width;
      var scaledViewport = page.getViewport(scale);

      // Prepare canvas using PDF page dimensions
      var canvas = document.getElementById('the-canvas');
      var context = canvas.getContext('2d');
      canvas.height = scaledViewport.height;
      canvas.width = scaledViewport.width;

      // Render PDF page into canvas context
      var renderContext = {
        canvasContext: context,
        viewport: scaledViewport
      };
      var renderTask = page.render(renderContext);
      renderTask.then(function () {
        // Close Spinner
        cti.fn_toggle_spinner('close', '', 500);
      });
    });
  }, function (reason) {
    // PDF loading error
    console.error(reason);
    // Close Spinner
    cti.fn_toggle_spinner('close', '', 500);
  });
}

