if (!!$data.$item.removalDate) {
  return
}

if(cti.store.variables.alerts === undefined) {
  cti.store.variables.alerts = []
}

var alert = cti.store.variables.alerts[0]

if (alert) {
  //var visibleString = 'alertRowVisible-' + cti.store.variables.alertToRemove.id;
  var hiddenString = 'alertRowHidden-' + alert.id;

  //var visible = document.getElementById(visibleString);
  var hidden = document.getElementById(hiddenString);

  if(hidden == null) {
    cti.store.variables.alerts = []
    return false;
  }

  //visible.classList.remove('selected');
  hidden.classList.remove('selected');

  if($data.$item.id != alert.id) {
    var alertToRemove = $data.$item;

    //var visibleString = 'alertRowVisible-' + {{$data.$item.id}};
    var hiddenString = 'alertRowHidden-' + {{$data.$item.id}};

    //var visible = document.getElementById(visibleString);
    var hidden = document.getElementById(hiddenString);

    //visible.classList.add('selected');
    hidden.classList.add('selected');

	cti.store.variables.alerts = [];
	cti.store.variables.alerts.push(alertToRemove);
  } else {
    cti.store.variables.alerts = []
  }
} else {
  var alertToRemove = $data.$item;

  //var visibleString = 'alertRowVisible-' + {{$data.$item.id}};
  var hiddenString = 'alertRowHidden-' + {{$data.$item.id}};

  //var visible = document.getElementById(visibleString);
  var hidden = document.getElementById(hiddenString);

  //visible.classList.add('selected');
  hidden.classList.add('selected');

  cti.store.variables.alerts = [];
  cti.store.variables.alerts.push(alertToRemove);
}

