const sliders = document.querySelectorAll('cti-slider')

// dialogIsReadOnly = the entire form is readonly because it is a historical view. This will only display the previous bar and *no* draggable slider.
// The sliderÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂs ÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂhistoriconlyÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂ property decides whether or not to display the draggable slider.
// The sliderÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂs readOnly property is applied when ÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂDeclined to AnswerÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂ is ticked and the slider should apply ÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂavoid-clicksÃÂÃÂÃÂÃÂ¢ÃÂÃÂÃÂÃÂÃÂÃÂÃÂÃÂ.

for (let i = 0; i < sliders.length; i++) {
  if (cti.store.variables.dialogIsReadOnly) {
    sliders[i].setAttribute('historiconly', 'true');
  } else {
    sliders[i].removeAttribute('historiconly');
  }
}

setTimeout(() => {

    let index = cti.store.pages.DialogFormOne && cti.store.pages.DialogFormOne.pickReviewCombo && cti.store.pages.DialogFormOne.pickReviewCombo.value !== undefined ? cti.store.pages.DialogFormOne.pickReviewCombo.value : -1;




    if(cti.store.variables.dialogIsReadOnly){
      index = 0;
      cti.store.pages.DialogFormOne.pickReviewCombo = {};
      cti.store.pages.DialogFormOne.pickReviewCombo.value = index;
    }

    cti.store.variables.currentDialogComboValue = index;

    cti.fn_setPreviousDialogVals(index)

    cti.fn_hideFreeSpinner()
  }, 250)

if(cti.store.variables.dialogIsReadOnly) {

  cti.utils.callAction("go-to-page", {"name":"Visit"})

} else {

  cti.utils.callAction("go-to-page", {"name":"DialogFormPre"})
}

cti.store.variables.dialogIsReadOnly = false

cti.store.variables.dialogFormFirstRun = false

if (window.debug) {
  debugger
}

if (!cti.store.pages.DialogFormOne) {
  cti.store.pages.DialogFormOne = {}
}
if (!cti.store.pages.DialogFormOne.pickReviewCombo) {
  cti.store.pages.DialogFormOne.pickReviewCombo = {}
}

let index = cti.store.variables.currentPreviousDialogIndex !== undefined ? cti.store.variables.currentPreviousDialogIndex : -1

if (index > -1) {
  index--
}

cti.store.variables.currentPreviousDialogIndex = index
