var selected_visit = cti.store.variables.selected_visit

if(!response.success) {  
  cti.fn_toggle_spinner('close')

  window.logManager.consoleLog(
    'API CALL: submitsafetyform FAILED - Reason: ' + response.message, 
    'error'
  )

  cti.utils.callAction("show-notification", {
    "name": "notification_error", 
    "title" : "Server Connection Error", 
    "text" : "Unable to submit Safety Form: " + response.message
  })

  if(selected_visit && selected_visit.attendees.attendee.clientId.value == response.clientId.value) {
    cti.store.variables.selected_visit.safety_form_status = "failed"
    cti.store.variables.selected_visit.safety_form_status_text = "Failed to Upload My Safety Plan, tap to retry..."
  }
} else {
  window.logManager.consoleLog(
    'API CALL: submitsafetyform SUCCESS', 
    'info'
  )

  var currentFormLength = 0

  if(selected_visit && selected_visit.attendees.attendee.clientId.value == response.clientId.value) {
    currentFormLength = cti.store.variables.selected_visit.patientData.mySafetyPlanForms.length

    if(response.data != undefined && response.data.length >= currentFormLength) {
      cti.store.variables.selected_visit.patientData.mySafetyPlanForms = response.data
    }

    for (var i in cti.store.variables.selected_visit.patientData.mySafetyPlanForms) {
      var safetyForm = cti.store.variables.selected_visit.patientData.mySafetyPlanForms[i];

      safetyForm.assessmentDate.displayDate = moment(safetyForm.assessmentDate.result).format("DD MMM YYYY HH:mm");
    }

    cti.store.variables.selected_visit.safety_form_status = "complete"
    cti.store.variables.selected_visit.safety_form_status_text = "Completed"

    delete cti.store.variables.selected_visit.safety_form_page
    cti.store.variables.v_submitSafetyFormPayload_clear = true
  } else {
    cti.fn_update_patient_data(response.clientId.value, 'safety', response.data, response.appointmentId)
  }

  cti.store.variables.v_submitSafetyFormPayload_clear = true
}

