        if(response.data.patientAlerts.patientAlert != undefined) {
          if(response.data.patientAlerts.patientAlert instanceof Array) {
            currentAlerts = response.data.patientAlerts.patientAlert
          } else {
            currentAlerts = []
            currentAlerts.push(response.data.patientAlerts.patientAlert)
          }

          // Re-order alerts array
          currentAlerts.sort(function(a, b) {
            return a.date > b.date ? -1 : a.date < b.date ? 1 : 0;
          });

          var displayAlerts = JSON.parse(JSON.stringify(currentAlerts))
          for (var i = displayAlerts.length; i-- > 0;) {
            var thisAlert = displayAlerts[i]

            if (thisAlert.removalDate) {
              displayAlerts.splice(i, 1)
            }
          }

          // Set open alerts temp variable in the store
          cti.store.variables.filter = "open"
          cti.store.variables.selected_visit.patientData.displayAlerts = displayAlerts

          // Update patient alerts
          cti.store.variables.selected_visit.patientData.alerts = currentAlerts

          // Refresh timeline
          cti.fn_prep_timeline()
        }
      }
    }
  } else {
    if(response.data != undefined) {
      if(response.data.patientAlerts != undefined) {
        if(response.data.patientAlerts.patientAlert != undefined) {
          if(response.data.patientAlerts.patientAlert instanceof Array) {
            currentAlerts = response.data.patientAlerts.patientAlert
          } else {
            currentAlerts = []
            currentAlerts.push(response.data.patientAlerts.patientAlert)
          }
        }
      }
    }

    if(currentAlerts.length > 0) {
      cti.fn_update_patient_data(response.clientId.value, 'alerts', currentAlerts)
    }
  } 

  window.logManager.consoleLog(
    'API CALL: removealert SUCCESS', 
    'info'
  )

  cti.store.variables.v_alertToRemove_clear = true
