var response = $data.$response
var selected_visit = cti.store.variables.selected_visit

if (!response.success) {
  window.logManager.consoleLog('API CALL: addprogressnote FAILED - Reason: ' + response.message, 'error')

  cti.utils.callAction('show-notification', {
    name: 'notification_error',
    title: 'Server Connection Error',
    text: 'Add Progress Note Failed: ' + response.message
  })
} else {
  let currentNotes = response.data && response.data.progressNotes && response.data.progressNotes.progressNote || null

  if (!!currentNotes && !currentNotes instanceof Array) {
    currentNotes = [currentNotes]
  }

  if (selected_visit && selected_visit.attendees.attendee.clientId.value == response.clientId.value) {
    if (currentNotes.length > 0) {
      cti.store.variables.selected_visit.patientData.progressNotes = currentNotes

      var documents = cti.store.variables.selected_visit.patientData.documents

      for (var i in currentNotes) {
        var note = currentNotes[i]

        if (note.associatedDocuments && note.associatedDocuments.associatedDocument) {
          var exists = false

          for (var x in documents) {
            var document = documents[x]

            if (document.documentId == note.associatedDocuments.associatedDocument.documentId) {
              exists = true
            }
          }

          if (!exists) {
            var newDocument = JSON.parse(JSON.stringify(note.associatedDocuments.associatedDocument))

            var dateCreated = newDocument.dateCreated.replace('T', ' ')
            newDocument.dateCreated = dateCreated
            var documentDate = newDocument.documentDate.replace('T', ' ')
            newDocument.documentDate = documentDate
            var insertedDate = newDocument.insertedDate.replace('T', ' ')
            newDocument.insertedDate = insertedDate

            documents.push(newDocument)
          }
        }
      }

      cti.fn_prep_timeline()
    }
  } else {
    if (currentNotes.length > 0) {
      cti.fn_update_patient_data(response.clientId.value, 'notes', currentNotes)
    }
  }

  window.logManager.consoleLog('API CALL: addprogressnote SUCCESS', 'info')

  cti.store.variables.v_progressNoteToAdd_clear = true

  if (cti.store.state.currentPage != 'Note') {
    delete cti.store.variables.note
    delete cti.store.variables.v_progressNoteToAdd_clear
    delete cti.store.variables.uiSubNoteTypes
  }
}

