window.pouchManager.loadFromDatabase('appointments', function (doc) {
  var appointments = doc.data
  var appointment = cti.store.variables.selected_visit

  // Check that we have a valid appointment
  for(var i in appointments) {
    if(appointments[i].appointmentId == appointment.appointmentId) {
      // Update the appointment data with the selected appointment
      appointments[i] = cti.store.variables.selected_visit
      break;
    }
  }

  // Update the pouch DB with new data
  window.pouchManager.saveToDatabase("appointments", appointments, function(rev) {
    window.logManager.consoleLog(
      'Pouch DB: Updated Appointment Data. Revision: ' + rev,
      'info'
    )

    setTimeout(function(){ 
      cti.store.variables.v_outcomePayload_clear = true
      cti.fn_load_appointments()
    }, 3000)

    $success()
  }, function(reason, e) {
    setTimeout(function(){ 
      cti.store.variables.v_outcomePayload_clear = true
      cti.fn_load_appointments()
    }, 3000)

    $success()

    window.logManager.consoleLog(
      'Pouch DB: Failed to Update Appointment Data - ' + reason + e,
      'error'
    )
  });
}, function (reason, err) {
  // Reset appointments
  setTimeout(function(){ 
    cti.store.variables.v_outcomePayload_clear = true
    cti.fn_load_appointments()
  }, 3000)

  $success()

  window.logManager.consoleLog(
    'Pouch DB: Error: Failed retriveing Appointments Data from Pouch - ' + reason + err,
    'error'
  )
})

