if(!response.success){
  // GetVisitList status set to failed: false
  cti.store.variables.getVisitListStatus = false

  cti.fn_toggle_spinner('close')

  window.logManager.consoleLog(
    'API CALL: getappointments FAILED - Reason: ' + response.message, 
    'error'
  )

  cti.utils.callAction("show-notification", {
    "name": "notification_error", 
    "title" : "Server Connection Error", 
    "text" : "Unable to get Appointments."
  })                           
} else {
  // GetVisitList status set to success: true
  cti.store.variables.getVisitListStatus = true

  window.logManager.consoleLog(
    'API CALL: getappointments SUCCESS', 
    'info'
  )

  window.pouchManager.loadFromDatabase('appointments', function (doc) {
    var appointments = []

    if(doc.data && doc.data.length > 0) {
      appointments = doc.data
      var updatedAppointments = []

      for(var i in response.data) {
        var responseAppointment = {}
        var responseAppointment = response.data[i]
        var exists = false

        for(var x in appointments) {
          var deviceAppointment = {}
          var deviceAppointment = appointments[x]

          if(deviceAppointment.appointmentId == responseAppointment.appointmentId) {
            exists = true
            break
          } 
        }

        var pushAppointment = {}

        if(exists) {
          if(deviceAppointment.status != "started") {
            deviceAppointment = responseAppointment
          } else {
            deviceAppointment.patientData = responseAppointment.patientData
          }

          pushAppointment = JSON.parse(JSON.stringify(deviceAppointment))
        } else {
          pushAppointment = JSON.parse(JSON.stringify(responseAppointment))
        }

        updatedAppointments.push(pushAppointment)
      }
    } else {
      updatedAppointments = response.data
    }

    window.pouchManager.saveToDatabase(
      "appointments", 
      updatedAppointments, function(rev) {
        window.logManager.consoleLog(
          'Pouch DB - Saved Appointment Data. Revision: ' + rev, 
          'info'
        )

        delete cti.store.api.api_rio.getappointments

        if(cti.store.variables.firstLogin) {
          delete cti.store.variables.firstLogin

          cti.utils.callAction("go-to-page", { name: "Set_PIN" })
        } else {
          cti.fn_load_appointments()
        }
      }, function(reason, e) {
        window.logManager.consoleLog(
          'Pouch DB - Failed to save Appointment Data - ' + reason, e, 
          'error'
        )

        cti.fn_toggle_spinner('close')
        delete cti.store.api.api_rio.getappointments
      })
  }, function (reason, err) {
    window.logManager.consoleLog(
      'Pouch DB - Appointments Pouch DB does not exist, creating new instance... - ' + reason + err,
      'Info'
    )

    window.pouchManager.saveToDatabase(
      "appointments", 
      cti.store.api.api_rio.getappointments.data, function(rev) {
        window.logManager.consoleLog(
          'Pouch DB - Saved Appointment Data. Revision: ' + rev, 
          'info'
        )

        delete cti.store.api.api_rio.getappointments

        if(cti.store.variables.firstLogin) {
          delete cti.store.variables.firstLogin

          cti.utils.callAction("go-to-page", { name: "Set_PIN" })
        } else {
          cti.fn_load_appointments()
        }
      }, function(reason, e) {
        window.logManager.consoleLog(
          'Pouch DB - Failed to save Appointment Data - ' + reason, e, 
          'error'
        )

        cti.fn_toggle_spinner('close')
        delete cti.store.api.api_rio.getappointments
      })
  })
}

