  'function fn_calc_visit_duration()', 
  'info'
);

if (!cti.store.pages.Outcome) {
  cti.store.pages.Outcome = {};
}

var startDate = cti.store.variables.selected_visit.arrivalTime;
var arrivalTime = moment(startDate).format("HH:mm");
var arrivalTimeHours = moment(arrivalTime, "HH:mm").format("HH");
var arrivalTimeMinutes = moment(arrivalTime, "HH:mm").format("mm");
var minsArrivalTime = parseInt(arrivalTimeMinutes);
var arrivalTimeLabel = "";

if(minsArrivalTime < 15) { arrivalTimeLabel = arrivalTimeHours+":00"; }
else if(minsArrivalTime >= 15 && minsArrivalTime < 30) { arrivalTimeLabel = arrivalTimeHours+":15"; }
else if(minsArrivalTime >= 30 && minsArrivalTime < 45) { arrivalTimeLabel = arrivalTimeHours+":30"; } 
else if(minsArrivalTime >= 45 && minsArrivalTime < 60) { arrivalTimeLabel = arrivalTimeHours+":45"; }

cti.store.pages.Outcome.arrivalTime = arrivalTimeLabel;
cti.store.pages.Outcome.actualTime = arrivalTimeLabel;

var now = moment().format("YYYY-MM-DDTHH:mm:ss");
var time = moment.utc(moment(now,"YYYY-MM-DDTHH:mm:ss").diff(moment(startDate,"YYYY-MM-DDTHH:mm:ss"))).format("HH:mm");
var mins = moment(time, "HH:mm").format("m");
var hours = moment(time, "HH:mm").format("h");

cti.store.pages.Outcome.duration_hours = {"label": hours, "value": hours };
cti.store.pages.Outcome.duration_minutes = {"label": mins, "value": mins };

