cti.store.variables.unsavedVisitData = ''

// Unsaved progress note...
let enteredData = null

if (cti.store.pages.Note) {
  const n = cti.store.pages.Note

  if (!!n) {
    enteredData = ['noteType', 'subType', 'isValidated', 'specialty', 'noteText', 'flagSignificantEvent', 'flagThirdParty', 'flagClientConceal'].some(e => !!n[e])

    if (!enteredData) {
      enteredData = n.list_riskType && n.list_riskType.numberOfItems > 0
    }

    if (!enteredData) {
      enteredData = (cti.store.media.btnPhoto && !!cti.store.media.btnPhoto.images) || (cti.store.media.btnAudio && !!cti.store.media.btnAudio.audio)
    }
  }
}

if (enteredData) {
  console.log(`You got some Note data!!`)
  cti.store.variables.unsavedVisitData += `${!!cti.store.variables.unsavedVisitData ? ', ' : ''}Note`
}

// Unsaved alert...
enteredData = null
if (cti.store.pages.Note) {
  const n = cti.store.pages.Alert

  if (!!n) {
    enteredData = ['type', 'priority', 'description'].some(e => !!n[e])
  }
}

if (enteredData) {
  console.log(`You got some Alert data!!`)
  cti.store.variables.unsavedVisitData += `${!!cti.store.variables.unsavedVisitData ? ', ' : ''}Alert`
}

// Check form statuses before outcoming.

var dialog = cti.store.variables.selected_visit.dialog_form_status
var safety = cti.store.variables.selected_visit.safety_form_status
var lifestyle = cti.store.variables.selected_visit.lifestyle_form_status

cti.store.variables.o_activities = []

if (dialog == 'failed' || dialog == 'inprogress') {
  cti.store.variables.unsavedVisitData += `${!!cti.store.variables.unsavedVisitData ? ', ' : ''}Dialog+`
} else if (dialog == 'complete' || dialog == 'sending') {
  var dialogObj = {
    group: undefined,
    value: 'RMDialog',
    label: 'DIALOG+'
  }

  cti.store.variables.o_activities.push(dialogObj)
}

if (safety == 'failed' || safety == 'inprogress') {
  cti.store.variables.unsavedVisitData += `${!!cti.store.variables.unsavedVisitData ? ', ' : ''}My Safety Plan`
} else if (safety == 'complete' || safety == 'sending') {
  var safetyObj = {
    group: undefined,
    value: 'RMMSP',
    label: 'My Safety Plan'
  }

  cti.store.variables.o_activities.push(safetyObj)
}

if (lifestyle == 'failed' || lifestyle == 'inprogress') {
  cti.store.variables.unsavedVisitData += `${!!cti.store.variables.unsavedVisitData ? ', ' : ''}Lifestyle Assessment Form`
} else if (lifestyle == 'complete' || lifestyle == 'sending') {
  var lifestyleObj = {
    group: undefined,
    value: 'RMLife',
    label: 'Lifestyle Assessment Form'
  }

  cti.store.variables.o_activities.push(lifestyleObj)
}

return cti.store.variables.unsavedVisitData === ''

const page = 'DialogFormOne'

// We can assume the page and previous data exist because the copy function should not be available if not. Today's value might not exist so check for that first - we're adding to, rather than replacing.
if (!cti.store.pages[page][question]) {
  cti.store.pages[page][question] = ''
