function setPosition(position){
  window.logManager.consoleLog(
    'function setPosition() Callback - Successfully saved current location', 
    'info'
  )

  if(position && position.coords && position.coords.latitude) {
  	cti.store.variables.latitude = position.coords.latitude.toString()
  } else {
    cti.store.variables.latitude = cti.store.schema.metadata.latitude || "53.3125156" // Sevelec HQ latitude
  }
  if(position && position.coords && position.coords.longitude) {
  	cti.store.variables.longitude = position.coords.longitude.toString()
  } else {
    cti.store.variables.longitude = cti.store.schema.metadata.longitude || "-1.3461527" // Sevelec HQ longitude
  }

  return true
}

if(cti.store.env.platform.name != "electron"){
