window.logManager.consoleLog('function fn_load_appointments(filter)', 'info')

if (window.debug) {
  debugger
}

if (cti.store.variables.v_appointmentDate === undefined) {
  cti.store.variables.v_appointmentDate = moment().format('YYYY-MM-DD')
  cti.store.variables.v_appointmentDateText = 'Today'
}

// Load appointments from pouch
window.pouchManager.loadFromDatabase(
  'appointments',
  function(doc) {
    // Set the appointments variable
    var appointments = doc.data

    // Filter the appointments with user friendly data and append
    // status values to them via the fn_set_appointments function
    var setAppointments = cti.fn_set_appointments(appointments)

    // Reset the v_selectedDayAppointments array
    cti.store.variables.v_selectedDayAppointments = []

    // For loop throught the array of appointments
    for (var i in setAppointments) {
      var thisAppointment = setAppointments[i]

      // If thisAppointment created date matches our filter date then
      // push this into the v_selectedDayAppointments array
      if (moment(thisAppointment.appointmentDateTime).format('YYYY-MM-DD') === cti.store.variables.v_appointmentDate) {
        var pushAppointment = JSON.parse(JSON.stringify(thisAppointment))
        cti.store.variables.v_selectedDayAppointments.push(pushAppointment)
      }
    }

    // Loop through the selectedDay appointments and create readable
    // index values for them
    for (var x in cti.store.variables.v_selectedDayAppointments) {
      var thisSelectedDayAppointment = cti.store.variables.v_selectedDayAppointments[x]
      thisSelectedDayAppointment.index = parseInt(x)
      var title = ''

      if (thisSelectedDayAppointment.patientData.demographics.title != undefined && thisSelectedDayAppointment.patientData.demographics.title != '') {
        title = ' (' + thisSelectedDayAppointment.patientData.demographics.title + ')'
      }

      var displayName = thisSelectedDayAppointment.patientData.demographics.surname + ', ' + thisSelectedDayAppointment.patientData.demographics.firstname + title
      thisSelectedDayAppointment.displayName = displayName

      thisSelectedDayAppointment.surname = thisSelectedDayAppointment.patientData.demographics.surname
      thisSelectedDayAppointment.mobilePhoneNumber = thisSelectedDayAppointment.patientData.demographics.mobilePhoneNumber
      thisSelectedDayAppointment.eveningPhoneNumber = thisSelectedDayAppointment.patientData.demographics.eveningPhoneNumber
      thisSelectedDayAppointment.officePhoneNumber = thisSelectedDayAppointment.patientData.demographics.officePhoneNumber

      // Delete all patientData
      delete thisSelectedDayAppointment.patientData

      // Remove additional appointment information
      delete thisSelectedDayAppointment.addressFormatted
      delete thisSelectedDayAppointment.age
      delete thisSelectedDayAppointment.attendees
      delete thisSelectedDayAppointment.dialog_form
      delete thisSelectedDayAppointment.gpDetails
      delete thisSelectedDayAppointment.healthcareProfessional
      delete thisSelectedDayAppointment.lifestyle_form
      delete thisSelectedDayAppointment.location
      delete thisSelectedDayAppointment.safety_form
      delete thisSelectedDayAppointment.warnings
      delete thisSelectedDayAppointment.createdAtDateTime
      delete thisSelectedDayAppointment.intendedDuration
    }

    // Update the pouch DB with new data
    window.pouchManager.saveToDatabase(
      'appointments',
      setAppointments,
      function(rev) {
        window.logManager.consoleLog('Pouch DB - Updated Appointment Data. Revision: ' + rev, 'info')

