  clientId: cti.store.variables.selected_visit.patientData.demographics.clientId,
  documentId: docObj.documentId,
  documentSerialNumber: docObj.serialNumber,
  documentDate: docObj.documentDate
};

// Remove rouge T if coming from the progress notes
