window.logManager.consoleLog("function fn_letOsOpenDocument()", "info");

if (window.debug_fn_letOsOpenDocument) {
  debugger;
}

if (!cti.store.variables.v_selectedDocument) {
  return;
}
const document = cti.store.variables.v_selectedDocument;

const base64 = document.base64 || "";

if (!base64) {
  return onError({ code: "File data not downloaded" });
}

const mimeType = cti.fn_sanitizeMimeType(document.fileType && document.fileType.toLowerCase() || "");
const fileExtension = cti.fn_getFileExtFromMimeType(mimeType);

if (!fileExtension) {
  return onError({
    code: `Filetype "${mimeType || ""}" is not supported`
  });
}

const fileName =
  (document.documentId || "document") +
  "-" +
  new Date().valueOf() +
  "." +
  fileExtension;

function gotFileSystem(fs, fileName) {
  fs.getFile(
    fileName,
    { create: true, exclusive: false },
    gotFileEntry,
    onError
  );
}

function gotFileEntry(fileEntry) {
  fileEntry.createWriter(fileWriter => {
    fileWriter.onwriteend = e => {
      openFile(cordova.file.dataDirectory + fileName, mimeType);
    };
    fileWriter.onerror = err => {
      console.log(`Write file failed: ${err.toString()}.`);
      return false;
    };

    fetch(`data:${mimeType};base64,${base64}`)
      .then(res => res.blob())
      .then(blob => fileWriter.write(blob));
  });
}

function onError(e) {
  console.log(`Document open failed with code: ${e.code}`);
  cti.utils.callAction("show-notification", {
    name: "notification_error",
    title: "Error",
    text: `Requested document could not be opened.`
  });
}

window.resolveLocalFileSystemURL(
  cordova.file.dataDirectory,
  dirEntry => {
    gotFileSystem(dirEntry, fileName);
  },
  onError
);

// Open file with the relevant application
function openFile(filePath, mimeType) {
  cordova.plugins.fileOpener2.open(filePath, mimeType, {
    error: e => {
      onError({ code: JSON.stringify(e) });
    }
  });
}

