window.logManager.consoleLog(
  'function fn_merge_busy_slots(bookedSlots, dow)', 
  'info'
);

// Process all sessions on the given day and return a list of free slots which can be booked into.

var diaryTemplate = cti.store.variables.v_diary_template

var selectedDay = diaryTemplate[dow] || {}
bookedSlots = bookedSlots || []
var freeSlots = []

// Ensure we've got at least one session to work with - if not, bomb straight out with empty free slots.
if (!selectedDay.sessions || !selectedDay.sessions[0] || !selectedDay.sessions[0].startTime) {
  return freeSlots
}

bookedSlots.sort(function(a, b) {
  if (!a.startDateTime || !b.startDateTime || !a.endDateTime || !b.endDateTime) {
    return 0
  }
  if (a.startDateTime < b.startDateTime) {
    return -1
  }
  if (a.startDateTime > b.startDateTime) {
    return 1
  }
  if (a.endDateTime < b.endDateTime) {
    return -1
  }
  if (a.endDateTime > b.endDateTime) {
    return 1
  }
  return 0
})

var getBusyPointerStartTime = function getBusyPointerStartTime(busyPointerIndex, sessionIndex) {
  // Ascertain if we have any booked slots to use and if so, do. Otherwise, use the current end of session marker.
  if (bookedSlots && bookedSlots.length > busyPointerIndex && bookedSlots[busyPointerIndex].startDateTime) {
    return moment(bookedSlots[busyPointerIndex].startDateTime)
  } else {
    return moment(selectedDay.sessions[sessionIndex].endTime, 'HH:mm')
  }
}

var freePointer = moment.duration(selectedDay.sessions[0].startTime)

var busyPointerIndex = 0

var busyPointer = moment.duration(moment(getBusyPointerStartTime(busyPointerIndex, 0)).format('HH:mm'))

selectedDay.sessions.forEach(function(session, index) {
  var sessionEndMins = moment.duration(session.endTime).asMinutes()

  while (freePointer.asMinutes() + session.slotLength <= sessionEndMins) {
    // Slot conflict checking...
    //
    // - Need to move freePointer to the end of each bookedSlot if it meets / overlaps.
    // - Can add the free slot if the current potential free slot starts before the current busy pointer (either an appt or the end of session marker) and that slot can exist in its entirety - i.e. adding
    //   slot length to the start time is still within those bounds.
    if (freePointer.asMinutes() <= busyPointer.asMinutes() && freePointer.asMinutes() + session.slotLength <= busyPointer.asMinutes()) {
      var hours = Math.floor(freePointer.asHours())
      var start = (hours < 10 ? '0' : '') + hours + moment.utc(freePointer.asMilliseconds()).format(':mm')

      // const slot = {
      //   day: `${selectedDay.name}`,
      //   time: `${start}`,
      //   duration: session.slotLength
      // }

      var slot = {
        code: '' + start,
        value: '' + start
      }

      freeSlots.push(slot)
    }

    // Update the pointers accordingly.

    // If we're at the start of or into the appt, move the free pointer to the end of this appt and move the busyPointer on to the next appt if there is one or alternatively, the end of the session.
    if ((freePointer.asMinutes() >= busyPointer.asMinutes() || freePointer.asMinutes() + session.slotLength >= busyPointer.asMinutes()) && !!bookedSlots[busyPointerIndex]) {
      // Set freePointer to be the latter of the end of the blocking appt and the next available slot start - to avoid odd minutes being made available.
      var busySlotEnd = moment.duration(moment(bookedSlots[busyPointerIndex].endDateTime, 'YYYY-MM-DD HH:mm:ss').format('HH:mm'))

      // Add as many slot lengths as we need to get past the current busy slot.
      while (freePointer.asMinutes() < busySlotEnd.asMinutes()) {
        freePointer = freePointer.add(session.slotLength, 'minutes')
      }

      busyPointerIndex++

      busyPointer = moment.duration(getBusyPointerStartTime(busyPointerIndex, index).format('HH:mm'))
    } else {
      freePointer.add(session.slotLength, 'minutes')
    }
  }

  // If we've got another session, update the pointer to either its current position or the start of the next session, whichever is later (to account for session overlaps).
  if (!!selectedDay.sessions[index + 1]) {
    var nextSessionStart = moment.duration(selectedDay.sessions[index + 1].startTime)
    if (nextSessionStart.asMinutes() > freePointer.asMinutes()) {
      freePointer = nextSessionStart
    }
  }
})

return freeSlots

