window.logManager.consoleLog(
  'function fn_normalise_diary_templ(importedTemplate)', 
  'info'
);

// importedTemplate = array of template objects

// Servelec days are 1-indexed.
var DAY_NUMBER_OFFSET = 1;

var diaryTemplate = [{
  name: 'Monday',
  sessions: []
}, {
  name: 'Tuesday',
  sessions: []
}, {
  name: 'Wednesday',
  sessions: []
}, {
  name: 'Thursday',
  sessions: []
}, {
  name: 'Friday',
  sessions: []
}, {
  name: 'Saturday',
  sessions: []
}, {
  name: 'Sunday',
  sessions: []
}];

// Normalise diary template data - we're only interested in so much of it.
diaryTemplate.forEach(function (dt, i) {
  // Add all of today's sessions.
  dt.sessions = importedTemplate.filter(function (e) {
    var appointmentsAreAllowed = function appointmentsAreAllowed() {
      return e.noAppointments != true;
    };
    var isCorrectDay = function isCorrectDay() {
      return e.dayNumber === (i + DAY_NUMBER_OFFSET).toString();
    };
    var isInMultiRange = function isInMultiRange() {
      // If this slot only covers one day, it's not multi so exit.
      if (e.endDayNumber === e.dayNumber) {
        return false;
      }

      return i + DAY_NUMBER_OFFSET >= parseInt(e.dayNumber) && i + DAY_NUMBER_OFFSET <= parseInt(e.endDayNumber);
    };

    return appointmentsAreAllowed() && (isCorrectDay() || isInMultiRange());
  }).map(function (e) {
    return {
      startTime: e.startTime,
      endTime: e.endTime,
      slotLength: parseInt(e.slotLength),
      addedFromDay: e.dayOfWeek
    };
  }).sort(function (a, b) {
    if (a.startTime < b.startTime) {
      return -1;
    }
    if (a.startTime > b.startTime) {
      return 1;
    }
    if (a.endTime < b.endTime) {
      return -1;
    }
    if (a.endTime > b.endTime) {
      return 1;
    }
    return 0;
  });
});

