var isTouchIdAvailable = function() {
  console.log(`Checking for biometrics...`);
  if (window.plugins && window.plugins.touchid) {
    console.log(`TouchID plugin available, checking for TouchID / FaceID...`);
    window.plugins.touchid.isAvailable(iOSverifyPrint, goPin);
  } else {
    console.log(`TouchID plugin NOT available...setting manual PIN`);
    goPin();
  }
};

var iOSverifyPrint = function() {
  console.log(
    `Running iOSVerifyPrint...specifically 'verifyFingerprintWithCustomPasswordFallbackAndEnterPasswordLabel()'`
  );

  // Seeing a race condition in iOS 11 where this attempts to load whilst the app is still loading (somehow) - seeing error -1004: User interaction is required.
  setTimeout(() => {
    window.plugins.touchid.verifyFingerprintWithCustomPasswordFallbackAndEnterPasswordLabel(
      "Scan your fingerprint please",
      "Enter App PIN",
      function(msg) {
        console.log(`TouchID success:`);
        console.log(JSON.stringify(msg));
        goVisits();
      },
      function(msg) {
        console.log(`TouchID failed:`);
        console.log(JSON.stringify(msg));
        // code: -8 (TouchID blocked)
        //  code: -3 (fallback)
        //  code: -2 (cancelled)
        if (msg.code < 0) {
          goPin();
        }
      }
    );
  }, 0);
};

var isFingerprintAuthAvailable = function() {
  console.log(`Checking for FingerprintAuth...`);
  FingerprintAuth.isAvailable(
    function(result) {
      console.log(result);

      if (
        result.isAvailable === true &&
        result.hasEnrolledFingerprints === true
      ) {
        var encryptConfig = {
          maxAttempts: 3,
          locale: "en_US",
          dialogTitle: "Fingerprint scan",
          dialogMessage: "Please scan your fingerprint"
        }; // See config object for required parameters

        FingerprintAuth.encrypt(
          encryptConfig,
          function(_fingerResult) {
            if (_fingerResult.withFingerprint) {
              goVisits();
            } else if (_fingerResult.withBackup) {
              goVisits();
            }
          },
          function(err) {
            if (err === "Cancelled") {
              console.log("FingerprintAuth cancelled");
              goPin();
            } else {
              console.log(`FingerprintAuth error: ${err}`);
              goPin();
            }
          }
        );
      } else {
        console.log("FingerprintAuth not available or user has not enrolled");
        goPin();
      }
    },
    function(err) {
      console.log("FingerprintAuth not available");
      goPin();
    }
  );
};

// If this isn't a fresh load then it's a resume. In that case, we only want to auth if we're not logged in / without PIN or if user has been inactive for the last n seconds where n = timeout.
if (!isFreshLoad) {
  console.log(`App resumed...`);
  if (
    cti.store.variables.lastUserActivity &&
    cti.store.variables.lastUserActivity >=
      new Date().getTime() - cti.store.variables.inactivityTimeout
  ) {
    console.log(`User activity within the timeout window...`);
    if (
      cti.store.variables.v_isAuthenticated &&
      cti.store.variables.v_userPIN
    ) {
      console.log(`User is already authenticated so will allow continue...`);

      /*       if (cti.store.state.currentPage === "Login") {
        goPin();
      } */
      if (cti.store.state.currentPage === "Login") {
        switch (cti.store.env.platform.name) {
          case "ios":
            isTouchIdAvailable();
            break;
          case "android":
            isFingerprintAuthAvailable();
            break;
          default:
            goPin();
        }
      }

      return;
    }
  }
} else {
  console.log(`App fresh load...`);
}

cti.fn_clearActivityTimeout();
cti.fn_stopActivityListener();

if (cti.store.variables.v_isAuthenticated) {
  if (cti.store.variables.v_userPIN) {
    console.log(
      `App resumed, already authenticated, PIN set - biometrics falling back to PIN...`
    );

    switch (cti.store.env.platform.name) {
      case "ios":
        isTouchIdAvailable();
        break;
      case "android":
        isFingerprintAuthAvailable();
        break;
      default:
        goPin();
    }
  } else {
    console.log(
      `App resumed, already authenticated, PIN not set - prompting to set...`
    );

    goSetPin();
  }
} else {
  console.log(`App resumed, NOT authenticated, login please`);

  cti.fn_restoreInitialState().then(res => goLogin());
}

