var activities = []
var dialog = false, 
    lifestyle = false, 
    safety = false

if(appointment.attendees) {
  if(appointment.attendees.attendee) {
    if(appointment.attendees.attendee.activities) {
      if(appointment.attendees.attendee.activities.activity) {
        if (appointment.attendees.attendee.activities.activity instanceof Array) {
          activities = appointment.attendees.attendee.activities.activity
        } else {
          var object = appointment.attendees.attendee.activities.activity
          activities.push(object)
        }
      }
    }
  }
}

if(appointment.dialog_form === undefined || appointment.lifestyle_form === undefined || appointment.safety_form === undefined) {
  for(var i in activities) {
    var code = activities[i].activityType.code

    if(code === "dialogplus" || code === "RMDialog") {
      dialog = true

      if(appointment.status == "complete") {
        cti.store.variables.selected_visit.dialog_form_status = "outcomed"
        cti.store.variables.selected_visit.dialog_form_status_text = "Appointment Outcomed"
      } else {
        cti.store.variables.selected_visit.dialog_form_status = "notcomplete"
        cti.store.variables.selected_visit.dialog_form_status_text = "Not Completed"
      }
    } else if(code === "phlifestyl" || code === "RMLife") {
      lifestyle = true

      if(appointment.status == "complete") {
        cti.store.variables.selected_visit.lifestyle_form_status = "outcomed"
        cti.store.variables.selected_visit.lifestyle_form_status_text = "Appointment Outcomed"
      } else {
        cti.store.variables.selected_visit.lifestyle_form_status = "notcomplete"
        cti.store.variables.selected_visit.lifestyle_form_status_text = "Not Completed"
      }
    } else if(code === "msp" || code === "RMMSP") {
      safety = true

      if(appointment.status == "complete") {
        cti.store.variables.selected_visit.safety_form_status = "outcomed"
        cti.store.variables.selected_visit.safety_form_status_text = "Appointment Outcomed"
      } else {
        cti.store.variables.selected_visit.safety_form_status = "notcomplete"
        cti.store.variables.selected_visit.safety_form_status_text = "Not Completed"
      }
    }
  }

  appointment.dialog_form = dialog;
  appointment.lifestyle_form = lifestyle;
  appointment.safety_form = safety;
} else {
  if(appointment.dialog_form && appointment.dialog_form_status) {
    dialog = appointment.dialog_form_status
  }
  if(appointment.lifestyle_form && appointment.lifestyle_form_status) {
    lifestyle = appointment.lifestyle_form_status
  }
  if(appointment.safety_form && appointment.safety_form_status) {
    safety = appointment.safety_form_status
  }

  if(dialog == "inprogress" || dialog == "failed") {
    if(appointment.dialog_form_page != undefined && appointment.status != "complete") {
      cti.store.pages.DialogFormPre = appointment.dialog_form_page.dialog_form_page_pre
      cti.store.pages.DialogFormOne = appointment.dialog_form_page.dialog_form_page_one
      cti.store.repeater.DialogFormTwo = appointment.dialog_form_page.dialog_form_repeater_pre
      cti.store.repeater.DialogFormThree = appointment.dialog_form_page.dialog_form_repeater_one
    } else if(appointment.status == "complete") {
      appointment.dialog_form_status = "outcomed"
      appointment.dialog_form_status_text = "Appointment Outcomed"
    }
  } else if(appointment.status == "complete") {
    appointment.dialog_form_status = "outcomed"
    appointment.dialog_form_status_text = "Appointment Outcomed"
  }

  if(lifestyle == "inprogress" || lifestyle == "failed") {
    if(appointment.lifestyle_form_page != undefined && appointment.status != "complete") {
      cti.store.pages.LifestyleFormOne = appointment.lifestyle_form_page.lifestyle_form_page_one
      cti.store.pages.LifestyleFormTwo = appointment.lifestyle_form_page.lifestyle_form_page_two
      cti.store.pages.LifestyleFormThree = appointment.lifestyle_form_page.lifestyle_form_page_three
      cti.store.pages.LifestyleFormFour = appointment.lifestyle_form_page.lifestyle_form_page_four
      cti.store.pages.LifestyleFormFive = appointment.lifestyle_form_page.lifestyle_form_page_five
      cti.store.pages.LifestyleFormSix = appointment.lifestyle_form_page.lifestyle_form_page_six

      cti.store.repeater.LifestyleFormThree = appointment.lifestyle_form_page.lifestyle_form_repeater_three
      cti.store.repeater.LifestyleFormFive = appointment.lifestyle_form_page.lifestyle_form_repeater_five
    } else if(appointment.status == "complete") {
      appointment.lifestyle_form_status = "outcomed"
      appointment.lifestyle_form_status_text = "Appointment Outcomed"
    }
  } else if(appointment.status == "complete") {
    appointment.lifestyle_form_status = "outcomed"
