function requestDirections(start, end, indx, markerContent) {
  directionsService.route(
    {
      origin: start,
      destination: end,
      travelMode: google.maps.DirectionsTravelMode.DRIVING
    },
    function(result) {
      if (!result || !result.status || ['ZERO_RESULTS', 'NO_ROUTES', 'NOT_FOUND'].indexOf(result.status) > -1 || !result.routes) {
        window.logManager.consoleLog(`function fn_render_map():requestDirections() - no routes for postcode ${end}.`, 'error')

        return false
      }

      var duration = result.routes[0].legs[0].duration
      var marker = new google.maps.Marker({
        position: {
          lat: result.routes[0].legs[0].end_location.lat(),
          lng: result.routes[0].legs[0].end_location.lng()
        },
        map: map,
        label: (indx + 1).toString(),
        icon: 'user-assets/pin.png'
      })

      var step = result.routes[0].legs[0].steps.length - 1
      var lat = result.routes[0].legs[0].steps[step].start_location.lat()
      var long = result.routes[0].legs[0].steps[step].start_location.lng()

      txt = new TxtOverlay(new google.maps.LatLng(lat, long), duration.text, 'durationBox', map)

      google.maps.event.addListener(
        marker,
        'click',
        (function(marker, i, duration) {
          //add the duration to the appointment data
          cti.store.variables.v_selectedDayAppointments[i].appointmentEta = moment()
            .add(duration.value, 'seconds')
            .format('hh:mm')

          return function() {
            var infowindow = new google.maps.InfoWindow()
            infowindow.setContent('<p>' + cti.store.variables.v_selectedDayAppointments[i].displayName + '<br/>' + cti.store.variables.v_selectedDayAppointments[i].appointmentType.value + '<br/>Travel Time: ' + duration.text + '</p>')
            infowindow.open(map, marker)
          }
        })(marker, indx, duration)
      )
      renderDirections(result)
    }
  )
}

