window.logManager.consoleLog('function fn_set_appointments(appointments)', 'info')

if (appointments === undefined) {
  appointments = {}
}

//appointments.forEach(function(obj, idx) {
for (var i in appointments) {
  var obj = appointments[i]

  // Check if the appointment has already been completed or not
  if (obj.attendees && obj.attendees.attendee && obj.attendees.attendee.outcome) {
    obj['status'] = 'complete'
  } else {
    if (obj.status != 'started') {
      obj['status'] = 'new'
    }
  }

  // Create friendly time stamps
  obj.shortTime = moment(obj.appointmentDateTime).format('HH:mm')
  obj.shortEndTime = moment(obj.appointmentDateTime)
    .add(obj.intendedDuration, 'minutes')
    .format('HH:mm')

  // No patientData means nothing more to process so continue.
  var patientData = obj.patientData || null
  if (patientData === null) {
    obj['patientData'] = {
      appointmentHistory: [],
      dialogPlusForms: [],
      progressNotes: [],
      contacts: [],
      allergies: [],
      demographics: [],
      alerts: [],
      documents: [],
      referrals: [],
      mySafetyPlanForms: []
    }
    return false
  }

  if (patientData.patient) {
    if (window.debug) {
      debugger
    }
    patientData.patient.forEach(e => (e.birthDateFormatted = moment(e.birthDate).format('DD-MM-YYYY')))
  }

  if (window.debug) {
    debugger
  }

  // Some helper mapping on DIALOG+ forms if we have any:
  if (patientData.dialogPlusForms) {
    patientData.dialogPlusForms.forEach((form, idx) => {
      let dateFormat = 'ddd Do MMM'

      // If we have any other entries with the same date, include the time.
      if (patientData.dialogPlusForms.find(f => f.assessmentDate.result === form.assessmentDate.result)) {
        dateFormat = 'ddd Do MMM HH:mm'
      }

      form.displayDate = form.assessmentDate && form.assessmentDate.result ? moment(form.assessmentDate.result).format(dateFormat) : ''
      form.index = idx
    })
  }

  var demographics = patientData.demographics || null

  if (demographics === null) {
    return false
  }

  // Set friendly formatted GP Details
  //
  // EDIT (LG): Re-work around this so that we can account for multiple healthcareProviders
  var currentGP
  var currentProvider

  if (demographics.healthcareProvider.healthcareProvider && Array.isArray(demographics.healthcareProvider.healthcareProvider)) {
    for (var i in demographics.healthcareProvider.healthcareProvider) {
      var provider = demographics.healthcareProvider.healthcareProvider[i]

      if (provider.endDate === '' || provider.endDate === undefined) {
        currentGP = provider.generalPractioner.value
        currentProvider = provider
        break
      }
    }
  } else {
    currentGP = (demographics.healthcareProvider && demographics.healthcareProvider.healthcareProvider && demographics.healthcareProvider.healthcareProvider.generalPractioner && demographics.healthcareProvider.healthcareProvider.generalPractioner.value) || ''
    currentProvider = demographics.healthcareProvider.healthcareProvider
  }

  var gp = currentGP

  obj.gpDetails = (gp !== '' ? gp + ' (' : '') + (currentProvider && currentProvider.practice && currentProvider.practice.value) + (gp !== '' ? ')' : '')

  // Set friendly formatted NHS number
  if (!!demographics.nhsNumber) {
    obj.nhsNumFormatted = demographics.nhsNumber.substring(0, 3) + ' ' + demographics.nhsNumber.substring(3, 6) + ' ' + demographics.nhsNumber.substring(6, 10)
  } else {
    obj.nhsNumFormatted = ''
  }

  // Set friendly formatted address
  obj.addressFormatted = [obj.patientData.demographics.address.addressLine1 || '', obj.patientData.demographics.address.addressLine2 || '', obj.patientData.demographics.address.addressLine3 || '', obj.patientData.demographics.address.addressLine4 || '']
    .filter(function(e) {
      return e.length > 0
    })
    .join(', ')

  // Set postcode for navigation
  obj.postCode = (demographics.address && demographics.address.postCode) || ''

  // Set patients age (note: now using moment for more precision)
  var now = moment().format('YYYY-MM-DD')
  obj.age = (demographics.birthDate && moment(now).diff(demographics.birthDate, 'years')) || ''
}

return appointments

