      { "destination" : "addcomm", "source" : "addcomm" },
      { "destination" : "YNPresent", "source" : "yhpresent" },
      { "destination" : "admissionassess", "source" : "admissionassess" },
      { "destination" : "SentCopy", "source" : "sentcopy" },
      { "destination" : "assessmentstatus", "source" : "assessmentstatus" },
      { "destination" : "assessmentDate", "source" : "assessmentDate", "typeOverride" : "date" }
    ]

    if (window.debug) {
      debugger
    }

    mapping.forEach(element => {
      var sourceEntry = thisForm[element.source]

      if(sourceEntry != undefined) {
        if(element.typeOverride && element.typeOverride == "toString") {
          formObject[element.destination] = (sourceEntry.result || "").toString()
        } else if(element.typeOverride && element.typeOverride == "toInt") {
          if(sourceEntry.result != "") {
            formObject[element.destination] = parseInt(sourceEntry.result)
          } else {
            formObject[element.destination] = 0
          }
        } else if(element.typeOverride && element.typeOverride == "date") {
          if(sourceEntry.displayDate != "" && sourceEntry.displayDate != undefined) {
            formObject[element.destination] = sourceEntry.displayDate
          } else {
            formObject[element.destination] = moment(sourceEntry.result).format('DD MMM YYYY hh:mmA')
          }
        } else {
          if((sourceEntry.type && sourceEntry.type == "multi") || (element.sourceTypeOverride && element.sourceTypeOverride === "multi")) {
            formObject[element.destination] = {
              "label" : sourceEntry.resultString || "",
              "value" : sourceEntry.result || ""
            }

            if(element.typeOverride) {
              if (element.typeOverride == "repeater") {
                formObject[element.destination].repeater = sourceEntry.repeater
              }
              if (element.typeOverride == "multi-toString") {
                formObject[element.destination].value = formObject[element.destination].value.toString()
              }
            }   
          } else {
            formObject[element.destination] = sourceEntry.result
          }
        }
      } else {
        formObject[element.destination] = ""
      }
    })

    cti.store.variables.v_lifestyleForms.push(formObject)
  }

  if(cti.store.variables.v_lifestyleForms.length === 0) {
