    sf_outofcrisis : thisForm.outofCrisis.result || 'No data provided',
    sf_earlyws : thisForm.earlyWs.result || 'No data provided',
    sf_earlyws_actionplan : thisForm.earlyWsap.result || 'No data provided',
    sf_getworse : thisForm.getWorse.result || 'No data provided',
    sf_getworse_actionplan : thisForm.getWorseAp.result || 'No data provided',
    sf_assisteddecision : thisForm.decisions.result || 'No data provided',
    sf_advancedirective : thisForm.advDir.result == "1" ? true : false,
    sf_serviceuseragreedplan : thisForm.suAgree.result == "1" ? true : false,
    sf_crisisnumber : thisForm.crisisNo.result || 'No data provided',
    sf_assessmentdate : moment(thisForm.assessmentDate.result).format('DD MMM YYYY hh:mmA') || 'No data provided'
  }

  cti.store.variables.v_safetyForms.push(formObject)
}

if(cti.store.variables.v_safetyForms.length === 0) {
  cti.fn_toggle_spinner('close')
  cti.utils.callAction("show-notification", {
    "name": "notification_warning", 
    "title" : "Warning: This patient currently has no Safety Forms", 
    "text" : "The current patient does not have any previously submitted Safety Forms."
  })
} else {
  cti.utils.callAction("go-to-page", {"name":"SafetyForm_View"});
}

window.logManager.consoleLog(
  'function fn_update_patient_data(clientId, type, data)', 
  'info'
)

window.pouchManager.loadFromDatabase('appointments', function (doc) {
  if(doc.data && doc.data.length > 0) {
    appointments = doc.data

    for(var i in appointments) {
      var thisAppointment = appointments[i]

      if(thisAppointment.attendees.attendee.clientId.value == clientId) {
        if(type == "alerts") {
          thisAppointment.patientData.alerts = data
        } else if(type == "notes") {
          thisAppointment.patientData.progressNotes = data
        } else if(type == "lifestyle") {
          thisAppointment.patientData.lifestyleAssessmentForms = data

          for (var x in thisAppointment.patientData.lifestyleAssessmentForms) {
            var lifestyleForm = thisAppointment.patientData.lifestyleAssessmentForms[x]
            var date = lifestyleForm.assessmentDate.result

            lifestyleForm.assessmentDate.displayDate = moment(date).format("DD MMM YYYY HH:mm")
          }

          if(thisAppointment.appointmentId == appointmentId) {
            thisAppointment.lifestyle_form_status = "complete"
            thisAppointment.lifestyle_form_status_text = "Completed"

            delete thisAppointment.lifestyle_form_page_one
            delete thisAppointment.lifestyle_form_page_two
            delete thisAppointment.lifestyle_form_page_three
            delete thisAppointment.lifestyle_form_page_four
            delete thisAppointment.lifestyle_form_page_five
            delete thisAppointment.lifestyle_form_page_six
          }
        } else if(type == "dialog") {
          thisAppointment.patientData.dialogPlusForms = data

          for (var x in thisAppointment.patientData.dialogPlusForms) {
            var dialogPlusForm = thisAppointment.patientData.dialogPlusForms[x]
            var date = dialogPlusForm.assessmentDate.result

            dialogPlusForm.assessmentDate.displayDate = moment(date).format("DD MMM YYYY HH:mm")
          }

          if(thisAppointment.appointmentId == appointmentId) {
            thisAppointment.dialog_form_status = "complete"
            thisAppointment.dialog_form_status_text = "Completed"

            delete thisAppointment.dialog_form_page
          }
        } else if(type == "safety") {
          thisAppointment.patientData.mySafetyPlanForms = data

          for (var x in thisAppointment.patientData.mySafetyPlanForms) {
            var mySafetyPlanForm = thisAppointment.patientData.mySafetyPlanForms[x]
            var date = mySafetyPlanForm.assessmentDate.result

            mySafetyPlanForm.assessmentDate.displayDate = moment(date).format("DD MMM YYYY HH:mm")
          }

          if(thisAppointment.appointmentId == appointmentId) {
            thisAppointment.safety_form_status = "complete"
            thisAppointment.safety_form_status_text = "Completed"

            delete thisAppointment.safety_form_page
          }
        }
      }
    }
  }

  window.pouchManager.saveToDatabase(
    "appointments", 
    appointments, function(rev) {
      window.logManager.consoleLog(
        'Pouch DB - Saved Appointment Data. Revision: ' + rev, 
        'info'
      )
    }, function(reason, e) {
      window.logManager.consoleLog(
        'Pouch DB - Failed to save Appointment Data - ' + reason, e, 
        'error'
      )
    })
}, function (reason, err) {
  window.logManager.consoleLog(
    'Pouch DB - Appointments Pouch DB does not exisit, creating new instance... - ' + reason + err,
    'Info'
  )

})

window.logManager.consoleLog(
  'function testPushResponse(notification)', 
  'info'
);

if (window.debug) {
  debugger
}

/*
{
  "event": "message",
  "payload": {
    "clientId": {
      "type": "MRN",
      "value": "RRRiO6MOD0922"
    },
    "notificationType": "AlertAdded"
  },
  "from": "888020801908",
  "message": "Patient Updated",
  "service": "GCM"
}
*/

/*
{
  "event": "message",
  "payload": {
    "trustODSCode": "dev",
    "appointments": [
      {
        "appointmentId": 2008948
      }
    ],
    "notificationType":"AppointmentCancelled"
  },
  "from": "888020801908",
  "message": "Patient Updated",
  "service": "GCM"
}
*/

// Notifty user we are updating device data
cti.fn_toggle_spinner('open', 'Update Received, refreshing Appointment list, please wait...');

// Grab the response
var pushPayload = JSON.parse(notification)
// COPY EVERYTHING BELOW THIS LINE TO NOTIFICATION ACTION
var notificationType = ''

try {
  if (typeof pushPayload !== 'object') {
    pushPayload = JSON.parse(pushPayload)
  }

  if (typeof pushPayload.payload !== 'object') {
    if (pushPayload.payload.indexOf('AppointmentAdded') != -1 && pushPayload.payload.indexOf('"AppointmentAdded"') == -1) {
      pushPayload.payload = pushPayload.payload.replace('AppointmentAdded', '"AppointmentAdded"')
    }

    pushPayload.payload = JSON.parse(pushPayload.payload)
  }

  if (pushPayload.payload && pushPayload.payload.notificationType) {
    notificationType = pushPayload.payload.notificationType
  } else {
    notificationType = 'Error'
  }

  if (notificationType === 'AppointmentCancelled') {
    // AppointmentCancelled: Remove the appointment from device, reload the
    // appointments data and navigate user back to visits page

    // Set the appointment to cancel
    if (pushPayload.payload.appointmentId) {
      cti.store.variables.appointmentId = pushPayload.payload.appointmentId.toString()
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Load appointments from pouch
    window.pouchManager.loadFromDatabase(
      'appointments',
      function(doc) {
        // Set the appointments array
        var appointments = doc.data

        // Find the appointment on device...
        for (var i in appointments) {
          if (appointments[i].appointmentId === cti.store.variables.appointmentId) {
            // ... and remove it from the main array
            appointments.splice(i, 1)
            break
          }
        }

        // Check that we aren't the the current visit, and if we are
        // then remove the data
        var selectedVisit = cti.store.variables.selected_visit

        if (selectedVisit) {
          if (selectedVisit.appointmentId === cti.store.variables.appointmentId) {
            cti.store.variables.return = true
          }
        }

        // Update the pouch DB with cancelled appointment
        window.pouchManager.saveToDatabase(
          'appointments',
          appointments,
          function(rev) {
            var appointmentId = cti.store.variables.appointmentId
            window.logManager.consoleLog('Pouch DB - Removed appointment ' + appointmentId + ' successfully. Revision: ' + rev, 'info')

            if (cti.store.variables.return || cti.store.state.currentPage == 'Visits') {
              cti.fn_load_appointments()
            }

            cti.fn_toggle_spinner('close', '')
            delete cti.store.variables.appointmentId
          },
          function(reason, e) {
            var appointmentId = cti.store.variables.appointmentId
            window.logManager.consoleLog('Pouch DB - Error updating pouch for appointment ' + appointmentId + '. Reason: ' + reason + e, 'error')

            if (cti.store.variables.return || cti.store.state.currentPage == 'Visits') {
              cti.fn_load_appointments()
            }

            cti.fn_toggle_spinner('close')
            delete cti.store.variables.appointmentId
          }
        )
      },
      function(reason, err) {
        window.logManager.consoleLog('Pouch DB - Failed retrieving Appointments Data from Pouch - ', reason + err, 'error')

        cti.fn_toggle_spinner('close')
        delete cti.store.variables.appointmentId
      }
    )
  } else if (notificationType === 'AppointmentUpdated' || notificationType === 'AppointmentOutcomed') {
    // AppointmentUpdated or AppointmentOutcomed: set the global payload to contain
    // the appointmentId and the exists flag (true in this case)

    // Check that we have a value appointmentId
    if (pushPayload.payload.appointmentId) {
      cti.store.variables.pushDataPayload = {
        pushType: 'appointment',
        appointmentId: pushPayload.payload.appointmentId,
        appointmentExists: true
      }
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Call the GetAppointment ActionFlow
    cti.utils.callAction('call-actionflow', { name: 'GetPushData' })
  } else if (notificationType === 'AppointmentAdded') {
    // AppointmentAdded: set the global payload to contain
    // the appointmentId and the exists flag (false in this case)

    // Check that we have a value appointmentId
    if (pushPayload.payload.appointmentId) {
      cti.store.variables.pushDataPayload = {
        pushType: 'appointment',
        appointmentId: pushPayload.payload.appointmentId,
        appointmentExists: false
      }
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Call the GetAppointment ActionFlow
    cti.utils.callAction('call-actionflow', { name: 'GetPushData' })
  } else if (notificationType === 'DemographicUpdated' || notificationType === 'GPUpdated' || notificationType === 'AddressUpdated') {
    // DemographicUpdated, GPUpdated, AddressUpdated, DeathRegistered: set the global payload to contain
    // the appointmentId and the exists flag (true for the demographics flag)

    // Check that we have a value appointmentId
    if (pushPayload.payload.clientId) {
      cti.store.variables.pushDataPayload = {
        pushType: 'patient',
        clientId: pushPayload.payload.clientId,
        appointmentExists: false,
        includeDemographics: true
      }
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Call the GetAppointment ActionFlow
    cti.utils.callAction('call-actionflow', { name: 'GetPushData' })
  } else if (notificationType == 'DeathRegistered') {
    // DeathRegistered: Remove any appointments from device, reload the
    // appointments data and navigate user back to visits page

    // Set the patient to remove
    if (pushPayload.payload.clientId) {
      cti.store.variables.clientId = pushPayload.payload.clientId
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Load appointments from pouch
    window.pouchManager.loadFromDatabase(
      'appointments',
      function(doc) {
        // Set the appointments array
        var appointments = doc.data

        // Find the appointments on device for the patient...
        for (var i in appointments) {
          if (appointments[i].attendees.attendee.clientId.value == cti.store.variables.clientId.value) {
            // ... and remove them from the main array
            appointments.splice(i, 1)
          }
        }

        // Check that we aren't the the current visit, and if we are
        // then remove the data
        var selectedVisit = cti.store.variables.selected_visit

        if (selectedVisit) {
          if (selectedVisit.attendees.attendee.clientId.value == cti.store.variables.clientId.value) {
            cti.store.variables.return = true
          }
        }

        // Update the pouch DB with cancelled appointment
        window.pouchManager.saveToDatabase(
          'appointments',
          appointments,
          function(rev) {
            var app = cti.store.variables.clientId.value
            window.logManager.consoleLog('Pouch DB - Removed patient appointments ' + app + ' successfully. Revision: ' + rev, 'info')

            if (cti.store.variables.return || cti.store.state.currentPage === 'Visits') {
              cti.fn_load_appointments()
            }

            cti.fn_toggle_spinner('close', '')
            delete cti.store.variables.clientId
          },
          function(reason, e) {
            var app = cti.store.variables.clientId.value
            window.logManager.consoleLog('Pouch DB - Error updating pouch for appointment ' + app + '. Reason: ' + reason + e, 'error')

            cti.fn_toggle_spinner('close')
            delete cti.store.variables.clientId
          }
        )
      },
      function(reason, err) {
        window.logManager.consoleLog('Pouch DB - Failed retriveing Appointments Data from Pouch - ', reason + err, 'error')

        cti.fn_toggle_spinner('close')
        delete cti.store.variables.clientId
      }
    )
  } else if (notificationType === 'AllergyAdded' || notificationType === 'AllergyRemoved') {
    // AllergyAdded, AllergyRemoved: set the global payload to contain
    // the appointmentId and the exists flag (true for the allergies flag)

    // Check that we have a value appointmentId
    if (pushPayload.payload.clientId) {
      cti.store.variables.pushDataPayload = {
        pushType: 'patient',
        clientId: pushPayload.payload.clientId,
        appointmentExists: false,
        includeAllergies: true
      }
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Call the GetAppointment ActionFlow
    cti.utils.callAction('call-actionflow', { name: 'GetPushData' })
  } else if (notificationType === 'AlertAdded' || notificationType === 'AlertRemoved') {
    // AlertAdded, AlertRemoved: set the global payload to contain
    // the appointmentId and the exists flag (true for the alerts flag)

    // Check that we have a value appointmentId
    if (pushPayload.payload.clientId) {
      cti.store.variables.pushDataPayload = {
        pushType: 'patient',
        clientId: pushPayload.payload.clientId,
        appointmentExists: false,
        includeAlerts: true
      }
    } else {
      cti.fn_toggle_spinner('close', '')
      return false
    }

    // Call the GetAppointment ActionFlow
    cti.utils.callAction('call-actionflow', { name: 'GetPushData' })
  } else {
    cti.fn_toggle_spinner('close')
  }
} catch (e) {
  cti.utils.callAction('show-notification', {
    name: 'notification_warning',
    title: 'Catch Error',
    text: e
  })
  cti.fn_toggle_spinner('close', '')
}

